import React, { useState, useEffect } from 'react';
import {
  Dialog,
  DialogTitle,
  DialogContent,
  DialogActions,
  Button,
  Typography,
  Box,
  Checkbox,
  FormControlLabel,
  useTheme,
  alpha,
} from '@mui/material';
import { Link as RouterLink } from 'react-router-dom';

interface CookieSettings {
  necessary: boolean;
  analytics: boolean;
  marketing: boolean;
}

const CookieConsent: React.FC = () => {
  const [open, setOpen] = useState(false);
  const [settings, setSettings] = useState<CookieSettings>({
    necessary: true,
    analytics: true,
    marketing: false,
  });
  const theme = useTheme();

  useEffect(() => {
    // Check if user has already made cookie choices
    const cookieConsent = localStorage.getItem('cookieConsent');
    if (!cookieConsent) {
      setOpen(true);
    }
  }, []);

  const handleAcceptAll = () => {
    setSettings({
      necessary: true,
      analytics: true,
      marketing: true,
    });
    saveCookieSettings({
      necessary: true,
      analytics: true,
      marketing: true,
    });
    setOpen(false);
  };

  const handleSaveSettings = () => {
    saveCookieSettings(settings);
    setOpen(false);
  };

  const saveCookieSettings = (settings: CookieSettings) => {
    localStorage.setItem('cookieConsent', JSON.stringify(settings));
    // Hier zou je Google Analytics of andere tracking kunnen initialiseren
    if (settings.analytics) {
      // Initialize analytics
    }
    if (settings.marketing) {
      // Initialize marketing cookies
    }
  };

  const handleChange = (name: keyof CookieSettings) => (event: React.ChangeEvent<HTMLInputElement>) => {
    if (name === 'necessary') return; // Necessary cookies can't be disabled
    setSettings(prev => ({
      ...prev,
      [name]: event.target.checked,
    }));
  };

  return (
    <Dialog
      open={open}
      maxWidth="sm"
      fullWidth
      sx={{
        '& .MuiDialog-paper': {
          borderRadius: 2,
          boxShadow: '0 8px 32px rgba(0,0,0,0.08)',
        },
      }}
    >
      <DialogTitle sx={{ 
        borderBottom: 1, 
        borderColor: 'divider',
        pb: 2,
        background: theme.palette.primary.main,
        color: 'white'
      }}>
        <Typography variant="h5" component="div" fontWeight="bold">
          Cookie instellingen
        </Typography>
      </DialogTitle>
      <DialogContent sx={{ mt: 2 }}>
        <Typography variant="body1" paragraph>
          Wij gebruiken cookies om je de beste ervaring op onze website te bieden. 
          Je kunt hieronder je voorkeuren aanpassen. Lees meer in ons{' '}
          <Box 
            component={RouterLink}
            to="/privacy" 
            sx={{
              color: theme.palette.primary.main,
              textDecoration: 'none',
              '&:hover': {
                textDecoration: 'underline',
              },
            }}
            onClick={() => setOpen(false)}
          >
            privacybeleid
          </Box>.
        </Typography>

        <Box sx={{ mt: 3 }}>
          <FormControlLabel
            control={
              <Checkbox 
                checked={settings.necessary} 
                disabled 
                sx={{
                  color: theme.palette.primary.main,
                  '&.Mui-checked': {
                    color: theme.palette.primary.main,
                  },
                }}
              />
            }
            label={
              <Box>
                <Typography variant="subtitle1" fontWeight="bold">Noodzakelijke cookies</Typography>
                <Typography variant="body2" color="text.secondary">
                  Deze cookies zijn nodig om de website goed te laten werken.
                </Typography>
              </Box>
            }
          />

          <FormControlLabel
            control={
              <Checkbox 
                checked={settings.analytics} 
                onChange={handleChange('analytics')}
                sx={{
                  color: theme.palette.primary.main,
                  '&.Mui-checked': {
                    color: theme.palette.primary.main,
                  },
                }}
              />
            }
            label={
              <Box>
                <Typography variant="subtitle1" fontWeight="bold">Analytische cookies</Typography>
                <Typography variant="body2" color="text.secondary">
                  Help ons de website te verbeteren door anonieme bezoekersstatistieken.
                </Typography>
              </Box>
            }
          />

          <FormControlLabel
            control={
              <Checkbox 
                checked={settings.marketing} 
                onChange={handleChange('marketing')}
                sx={{
                  color: theme.palette.primary.main,
                  '&.Mui-checked': {
                    color: theme.palette.primary.main,
                  },
                }}
              />
            }
            label={
              <Box>
                <Typography variant="subtitle1" fontWeight="bold">Marketing cookies</Typography>
                <Typography variant="body2" color="text.secondary">
                  Laat ons relevante advertenties tonen op andere websites.
                </Typography>
              </Box>
            }
          />
        </Box>
      </DialogContent>
      <DialogActions sx={{ 
        p: 3, 
        borderTop: 1, 
        borderColor: 'divider',
        gap: 2,
      }}>
        <Button 
          onClick={handleSaveSettings}
          variant="outlined"
          sx={{
            borderColor: theme.palette.primary.main,
            color: theme.palette.primary.main,
            '&:hover': {
              borderColor: theme.palette.primary.dark,
              backgroundColor: alpha(theme.palette.primary.main, 0.04),
            },
          }}
        >
          Opslaan
        </Button>
        <Button 
          onClick={handleAcceptAll}
          variant="contained"
          sx={{
            backgroundColor: theme.palette.primary.main,
            color: 'white',
            '&:hover': {
              backgroundColor: theme.palette.primary.dark,
            },
          }}
        >
          Accepteer alles
        </Button>
      </DialogActions>
    </Dialog>
  );
};

export default CookieConsent;
