import React from 'react';
import { Container, Typography, Button, Box, Grid, useTheme } from '@mui/material';
import { Link as RouterLink } from 'react-router-dom';
import { keyframes } from '@mui/system';

const fadeIn = keyframes`
  from {
    opacity: 0;
    transform: translateY(20px);
  }
  to {
    opacity: 1;
    transform: translateY(0);
  }
`;

const Hero = () => {
  const theme = useTheme();

  return (
    <Box
      className="hero-section"
      sx={{
        background: 'linear-gradient(135deg, #6B46C1 0%, #3B82F6 100%)',
        color: 'white',
        position: 'relative',
        overflow: 'hidden',
        '&::before': {
          content: '""',
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: 'url("/pattern.svg")',
          opacity: 0.1,
        }
      }}
    >
      <Container maxWidth="lg">
        <Grid container spacing={4} sx={{ minHeight: '80vh', alignItems: 'center' }}>
          <Grid item xs={12} md={6} sx={{ animation: `${fadeIn} 1s ease-out` }}>
            <Box>
              <Typography 
                variant="h1" 
                component="h1" 
                sx={{
                  fontWeight: 800,
                  fontSize: { xs: '2.5rem', md: '3.5rem' },
                  mb: 2,
                  lineHeight: 1.2
                }}
              >
                Maak Jouw Perfecte Website
              </Typography>
              <Typography 
                variant="h5" 
                sx={{ 
                  mb: 4, 
                  opacity: 0.9,
                  fontSize: { xs: '1.2rem', md: '1.5rem' },
                  fontWeight: 400
                }}
              >
                CreÃ«er een professionele website die bij jouw bedrijf past. 
                Modern, snel en gebruiksvriendelijk.
              </Typography>
              <Box sx={{ display: 'flex', gap: 2, flexWrap: 'wrap' }}>
                <Button
                  variant="contained"
                  size="large"
                  component={RouterLink}
                  to="/diensten"
                  sx={{
                    backgroundColor: 'white',
                    color: theme.palette.primary.main,
                    fontSize: '1.1rem',
                    py: 1.5,
                    px: 4,
                    '&:hover': {
                      backgroundColor: 'rgba(255, 255, 255, 0.9)',
                    }
                  }}
                >
                  Start Nu
                </Button>
                <Button
                  variant="outlined"
                  size="large"
                  component={RouterLink}
                  to="/contact"
                  sx={{
                    borderColor: 'white',
                    color: 'white',
                    fontSize: '1.1rem',
                    py: 1.5,
                    px: 4,
                    '&:hover': {
                      borderColor: 'rgba(255, 255, 255, 0.9)',
                      backgroundColor: 'rgba(255, 255, 255, 0.1)',
                    }
                  }}
                >
                  Neem Contact Op
                </Button>
              </Box>
            </Box>
          </Grid>
          <Grid item xs={12} md={6} sx={{ animation: `${fadeIn} 1s ease-out 0.3s both` }}>
            <Box
              component="img"
              src="/hero-image.svg"
              alt="Website bouwen illustratie"
              sx={{
                width: '100%',
                maxWidth: '600px',
                display: 'block',
                margin: 'auto',
                filter: 'drop-shadow(0 20px 40px rgba(0,0,0,0.1))'
              }}
            />
          </Grid>
        </Grid>
      </Container>
    </Box>
  );
};

export default Hero;
